//Using + operator for concatenation and addition
package training;

public class Plusop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 5, y = 8;
		System.out.println("Concatenation (x+y) = " + x + y);
		System.out.println("Addition (x+y) = " + (x+y));
	}
}
